CREATE VIEW qgep.is_dictionary_value_list AS
  SELECT p.relname AS vl_name, vl.*
  FROM qgep.is_value_list_base vl, pg_class p
  WHERE vl.tableoid = p.oid;


CREATE TABLE qgep.is_dictionary_od_table
(
  id integer NOT NULL,
  tablename text,
  name_en text,
  shortcut_en character(2),
  name_de text,
  shortcut_de character(2),
  name_fr text,
  shortcut_fr character(2),
  CONSTRAINT pkey_qgep_is_dictonary_id PRIMARY KEY (id),
  CONSTRAINT unq_qgep_is_dictonary_tablename UNIQUE (tablename)
)
WITH (
  OIDS = False
);

