 INSERT INTO qgep.is_dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_description_en, value_description_de, value_description_fr, value_description_it) VALUES (2,2261,3363,'wastewater_structure','structure_condition','Z0','Z0','','Z0','Z0','Z0','Z0','Z0','','yyy_Nicht mehr funktionstüchtig: Der Kanal ist bereits oder demnächst nicht mehr durchgängig: Kanal eingestürzt, totale Verwurzelung oder andere Abflusshindernisse. Der Kanal verliert Wasser (Exfiltration / mögliche Grundwasserverschmutzung).','Nicht mehr funktionstüchtig: Das Abwasserbauwerk ist bereits oder demnächst nicht mehr durchgängig: Bauwerk eingestürzt, totale Verwurzelung oder andere Abflusshindernisse. Das Bauwerk verliert Wasser (Exfiltration / mögliche Grundwasserverschmutzung).','Plus en état de fonctionner: L''ouvrage d''assainissement nest déjà plus parcourable ou ne le sera plus dans peu de temps: Ouvrage d''assainissement écroulé, totalement bouché par des racines ou par dautres obstacles à lécoulement. L''ouvrage d''assainissement perd de leau (exfiltration / pollution probable de leau souterraine)','Non più funzionante: la canalizzazione è ostruita o lo sarà entro breve termine:canale crollato, completamente pervaso da radici o altri ostacoli al deflusso. Il canale perde acqua (esfiltrazione / possibile inquinamento delle acque sotterranee)');
 INSERT INTO qgep.is_dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_description_en, value_description_de, value_description_fr, value_description_it) VALUES (2,2261,3359,'wastewater_structure','structure_condition','Z1','Z1','','Z1','Z1','Z1','Z1','Z1','','yyy_Starke Mängel: Bauliche Schäden, bei welchen die statische Sicherheit, Hydraulik oder Dichtheit nicht mehr gewährleistet ist: Rohrbrüche axial oder radial, Rohrdeformationen, visuell sichtbare Wassereintritte oder Wasseraustritte, Löcher in der Rohrwand, stark vorstehende seitliche Anschlüsse, starke Verwurzelungen, Rohrwand stark ausgewaschen. Ungeeignetes Rohrmaterial.','Starke Mängel: Bauliche Schäden, bei welchen die statische Sicherheit, Hydraulik oder Dichtheit nicht mehr gewährleistet ist: Brüche axial oder radial, (Rohr-)deformationen, visuell sichtbare Wassereintritte oder Wasseraustritte, Löcher in der Wand, stark vorstehende seitliche Anschlüsse, starke Verwurzelungen, Wand stark ausgewaschen. Ungeeignetes (Rohr-)material.','Graves défauts: Dommages structurels ne garantissant plus la sécurité statique, lhydraulique ou létanchéité: cassures, déformations, entrées ou sorties visibles deau, trous dans la paroi, forte saillie des raccords latéraux, fort développement des racines, paroi  fortement érodée par lavage. Matériau  inapproprié','Gravi difetti: difetti costruttivi a causa dei quali la sicurezza statica, l''idraulica o la tenuta non sono piu assicurate: rotture del tubo assiali o radiali, deformazioni del tubo, infiltrazioni o perdite d''acqua visibili, fori nella parete del tubo, raccordi laterali molto sporgenti, notevole pervasione radicale, parete del tubo molto corrosa. Materiale del tubo inadatto.');
 INSERT INTO qgep.is_dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_description_en, value_description_de, value_description_fr, value_description_it) VALUES (2,2261,3360,'wastewater_structure','structure_condition','Z2','Z2','','Z2','Z2','Z2','Z2','Z2','','yyy_Mittlere Mängel: Bauliche Mängel, welche die Statik, Hydraulik oder Dichtheit beeinträchtigen: breite Rohrfugen, nicht verputzte Einläufe, Risse, leichte Abflusshindernisse wie Verkalkungen, vorstehende seitliche Anschlüsse, leichte Rohrwandbeschädigungen, einzelne Wurzeleinwüchse, Rohrwand ausgewaschen usw.','Mittlere Mängel: Bauliche Mängel, welche die Statik, Hydraulik oder Dichtheit beeinträchtigen: breite (Rohr-)fugen, nicht verputzte Einläufe, Risse, leichte Abflusshindernisse wie Verkalkungen, vorstehende seitliche Anschlüsse, leichte Wandbeschädigungen, einzelne Wurzeleinwüchse, (Rohr-)wand ausgewaschen usw.','Défauts moyens: Défauts structurels affectant la statique, lhydraulique ou létanchéité: joints avec canalisation renflés, entrées non nettoyées, fissures, légers obstacles à lécoulement (entartrages, saillie des raccords latéraux, légers dommages à la paroi, développement localisé des racines, paroi érodée par lavage, etc.)','Difetti medi: difetti costruttivi che pregiudicano la statica, l''idraulica o la tenuta: giunti dei tubi larghi, entrate non sigillate, fessurazioni, leggeri ostacoli al deflusso come incrostazioni calcaree, raccordi laterali sporgenti, pareti dei tubi leggermente danneggiate, singole penetrazioni di radici, parete del tubo corrosa ecc.');
 INSERT INTO qgep.is_dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_description_en, value_description_de, value_description_fr, value_description_it) VALUES (2,2261,3361,'wastewater_structure','structure_condition','Z3','Z3','','Z3','Z3','Z3','Z3','Z3','','yyy_Leichte Mängel: Bauliche Mängel oder Vorkommnisse, welche für die Dichtheit, Hydraulik oder Rohrstatik einen unbedeutenden Einfluss haben: breite Rohrfugen, schlecht verputzte seitlichen Anschlüsse, leichte Deformation bei Kunststoffleitungen, leichte Auswaschungen etc.','Leichte Mängel: Bauliche Mängel oder Vorkommnisse, welche für die Dichtheit, Hydraulik oder Statik einen unbedeutenden Einfluss haben: breite (Rohr-)fugen, schlecht verputzte seitlichen Anschlüsse, leichte Deformation bei Bauwerken aus Kunststoff, leichte Auswaschungen etc.','Défauts légers: Défauts ou événements structurels ayant une influence insignifiante sur létanchéité, lhydraulique ou la statique de l''ouvrage d''assainissement: joints avec canalisations renflées, raccords latéraux mal nettoyés, légères déformations sur matériaux plastiques, légères érosions dues au lavage, etc','Difetti lievi: difetti costruttivi o anomalie senza influenuza significativa sulla tenuta, l''idraulica o la statica dei tubi: giunti larghi dei tubi, raccordi laterali male sigillati, lievi deformazioni di condotte sintetiche, lievi corrosioni ecc.');
