 INSERT INTO qgep.is_dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_description_en, value_description_de, value_description_fr, value_description_it) VALUES (50,5958,6203,'pump','usage_current','discharged_combined_wastewater','discharged_combined_wastewater','DCW','entlastetes_Mischabwasser','EW','eaux_mixtes_deversees','EUD','acque_miste_scaricate','','yyy_Wasser aus einem Entlastungsbauwerk, welches zum Vorfluter geführt wird. In diesen Kanal darf kein Schmutzabwasser eingeleitet werden.','Wasser aus einem Entlastungsbauwerk, welches zum Vorfluter geführt wird. In diesen Kanal darf kein Schmutzabwasser eingeleitet werden.','Eau conduite entre un ouvrage de déversement et un exutoire au milieu récepteur. Aucune eau usée ne doit être déversée dans cette canalisation','Acqua scaricata da un manufatto di scarico e immessa in un corso d''acqua. In questa canalizzazione non possono assere immesse acque luride.');
 INSERT INTO qgep.is_dictionary_od_values (class_id, attribute_id, value_id, table_name, field_name, value_name, value_name_en, shortcut_en, value_name_de, shortcut_de, value_name_fr, shortcut_fr, value_name_it, shortcut_it, value_description_en, value_description_de, value_description_fr, value_description_it) VALUES (50,5958,6204,'pump','usage_current','industrial_wastewater','industrial_wastewater','','Industrieabwasser','CW','eaux_industrielles','EUC','acque_industriali','','yyy_Unter Industrieabwasser werden alle Abwässer verstanden, die bei Produktions- und Verarbeitungsprozessen in der Industrie anfallen. Industrieabwässer müssen i.d.R. vorbehandelt werden, bevor sie in öffentliche Kläranlagen eingeleitet werden können (siehe Indirekteinleiter). Bei direkter Einleitung in Gewässer (siehe Direkteinleiter) ist eine umfangreiche Reinigung in speziellen werkseigenen Kläranlagen erforderlich.','Unter Industrieabwasser werden alle Abwässer verstanden, die bei Produktions- und Verarbeitungsprozessen in der Industrie anfallen. Industrieabwässer müssen i.d.R. vorbehandelt werden, bevor sie in öffentliche Kläranlagen eingeleitet werden können (siehe Indirekteinleiter). Bei direkter Einleitung in Gewässer (siehe Direkteinleiter) ist eine umfangreiche Reinigung in speziellen werkseigenen Kläranlagen erforderlich.','Eaux industrielles ou chimiques: Les eaux industrielles comprennent toutes les eaux produites lors d''un processus de production ou de fabrication. Les eaux industrielles doivent être prétraitées avant d''être déversées dans les canalisations en direction de la STEP communal (voir évacuation indirecte). En cas de déversement direct dans une eaux de surface, un traitement dans une station de traitement spécifique doit être effectué (voir évacuation directe).','Acque industriali o chimiche: comprendono le acque prodotte durante processi industriali o di fabbricazione. Acque industriali devono di regole essere pretrattate, prima di poter essere convogliate in un IDA. Nel caso di un''immissione diretta nel ricettore è necessario un trattamento in speciali impianti industriali.');
